import {useState} from "react";

const [count,setCount] = useState(0);

function increase(){
  setCount(count + 1);
}

function decrease(){
  setCount(count - 1);
}

function App() {
  return(
    <div className="container">
    <h1>0</h1>
    <button>+</button>
  </div>
  )
}

export default App;
